import React, { useState } from 'react';
import HabitTracker from './HabitTracker';
import TaskView from './TaskView'; // Import the TaskView component

const Homepage = () => {
    const [activeTab, setActiveTab] = useState('today'); // Default tab is 'today'

    const handleTabChange = (tab) => {
        setActiveTab(tab);
    };

    return (
        <div className="homepage-container">
            <div className="homepage-controls"> {/* Change to homepage-controls */}
                <button onClick={() => handleTabChange('today')} className={activeTab === 'today' ? 'active' : ''}>Today</button>
                <button onClick={() => handleTabChange('week')} className={activeTab === 'week' ? 'active' : ''}>Week</button>
                <button onClick={() => handleTabChange('month')} className={activeTab === 'month' ? 'active' : ''}>Month</button>
            </div>
            <div className="homepage-content">
                {activeTab === 'today' && <HabitTracker viewMode="default" />} {/* Default view for today */}
                {activeTab === 'week' && <TaskView />} {/* TaskView component */}
                {activeTab === 'month' && <HabitTracker viewMode="monthly" />} {/* Monthly view */}
            </div>
        </div>
    );
};

export default Homepage;
