import React, { useState, useEffect } from 'react';
import apiService from '../apiService';
import './Journal.css';
import { useCalendar } from './CalendarContext';

const Journal = () => {
    const { selectedDate } = useCalendar();
    const [journalEntry, setJournalEntry] = useState("");

    useEffect(() => {
        const fetchJournalEntry = async () => {
            try {
                console.log('Fetching journal entry for date:', selectedDate);
                const response = await apiService.getJournalEntryByDate(selectedDate);
                console.log('API Response:', response);

                if (response && response.entry_text !== undefined) {
                    setJournalEntry(response.entry_text || "");
                } else {
                    setJournalEntry("");
                }
            } catch (error) {
                console.error('Error fetching journal entry:', error);
            }
        };

        fetchJournalEntry();
    }, [selectedDate]);

    const handleSaveJournalEntry = async () => {
        try {
            const journalData = {
                entry_text: journalEntry,
                date: selectedDate,
            };

            console.log('Saving journal entry:', journalData);
            await apiService.createOrUpdateJournalEntry(selectedDate, journalData);
            console.log('Entry saved successfully');
        } catch (error) {
            console.error('Error saving journal entry:', error);
        }
    };

    return (
        <div className="journal-container">
            <textarea
                value={journalEntry}
                onChange={(e) => setJournalEntry(e.target.value)}
                placeholder="Write your thoughts here..."
            />
            <button onClick={handleSaveJournalEntry}>Save Entry</button>
        </div>
    );
};

export default Journal;
