import React, { useEffect, useState } from 'react';
import { getHabits, getHabitEntriesByDateRange } from '../apiService';
import TaskView from './TaskView'; // Assuming TaskView is integrated here
import HabitView from './HabitView';
import HabitEntryView from './HabitEntryView';

const HabitTracker = () => {
    const [habits, setHabits] = useState([]);
    const [habitEntries, setHabitEntries] = useState([]);
    const { selectedDate } = useCalendar(); // Use the calendar context to get the date

    useEffect(() => {
        const fetchHabits = async () => {
            try {
                const habitResponse = await getHabits();
                setHabits(habitResponse.data);
            } catch (error) {
                console.error('Error fetching habits:', error);
            }
        };

        const fetchHabitEntries = async () => {
            try {
                const habitEntriesResponse = await getHabitEntriesByDateRange(selectedDate, selectedDate);
                setHabitEntries(habitEntriesResponse.data);
            } catch (error) {
                console.error('Error fetching habit entries:', error);
            }
        };

        fetchHabits();
        fetchHabitEntries();
    }, [selectedDate]);

    return (
        <div>
            <HabitView habits={habits} />
            <HabitEntryView habitEntries={habitEntries} />
            <TaskView />  {/* TaskView included here */}
        </div>
    );
};

export default HabitTracker;
