import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WeekView.css'; // Ensure this file exists for styling

const WeekView = ({ currentDate }) => {
  const [tasks, setTasks] = useState({
    Monday: [], Tuesday: [], Wednesday: [], Thursday: [], Friday: [], Saturday: [], Sunday: []
  });

  const [newTask, setNewTask] = useState({
    Monday: '', Tuesday: '', Wednesday: '', Thursday: '', Friday: '', Saturday: '', Sunday: ''
  });

  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

  const startOfWeek = new Date(currentDate);
  startOfWeek.setDate(startOfWeek.getDate() - startOfWeek.getDay() + 1); // Monday as the first day of the week

  const getDateForDay = (dayIndex) => {
    const date = new Date(startOfWeek);
    date.setDate(startOfWeek.getDate() + dayIndex);
    return date.getDate(); // Only return the day of the month
  };

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/api/tasks/?date=${currentDate.toISOString().split('T')[0]}`);
        if (response.data) {
          const fetchedTasks = {
            Monday: [],
            Tuesday: [],
            Wednesday: [],
            Thursday: [],
            Friday: [],
            Saturday: [],
            Sunday: []
          };

          response.data.forEach(task => {
            const taskDate = new Date(task.date);
            const dayName = daysOfWeek[taskDate.getDay() - 1];
            fetchedTasks[dayName].push({ task: task.name, completed: task.completed });
          });

          setTasks(fetchedTasks);
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [currentDate, daysOfWeek]);

  const handleAddTask = async (day, task) => {
    if (task) {
      const date = new Date(startOfWeek);
      date.setDate(date.getDate() + daysOfWeek.indexOf(day));

      try {
        const response = await axios.post('http://localhost:8000/api/tasks/', {
          name: task,
          completed: false,
          date: date.toISOString().split('T')[0] // Ensure correct ISO date format
        });

        if (response.status === 201) {
          setTasks({ ...tasks, [day]: [...tasks[day], { task, completed: false }] });
          setNewTask({ ...newTask, [day]: '' }); // Clear the input field
        } else {
          console.error('Failed to add task:', response);
        }
      } catch (error) {
        console.error('Error adding task:', error);
      }
    }
  };

  const handleToggleTask = (day, index) => {
    const newTasks = tasks[day].map((t, i) => i === index ? { ...t, completed: !t.completed } : t);
    setTasks({ ...tasks, [day]: newTasks });
  };

  const handleInputChange = (day, event) => {
    setNewTask({ ...newTask, [day]: event.target.value });
  };

  const handleInputKeyPress = (day, event) => {
    if (event.key === 'Enter') {
      handleAddTask(day, newTask[day]);
    }
  };

  return (
    <div className="week-view">
      <table className="week-table">
        <thead>
          <tr>
            <th></th> {/* Empty header for day initials and date */}
            <th></th>
          </tr>
        </thead>
        <tbody>
          {daysOfWeek.map((day, index) => (
            <tr key={day}>
              <td className="day-column">
                {day.charAt(0)} {/* Display first letter of the day */}
                <br />
                {getDateForDay(index)} {/* Display the day of the month */}
              </td>
              <td>
                <ul>
                  {tasks[day].map((task, taskIndex) => (
                    <li key={taskIndex} className={task.completed ? 'completed-task' : ''} onClick={() => handleToggleTask(day, taskIndex)}>
                      {task.task}
                    </li>
                  ))}
                </ul>
                <input
                  type="text"
                  value={newTask[day]}
                  onChange={(event) => handleInputChange(day, event)}
                  onKeyPress={(event) => handleInputKeyPress(day, event)}
                  placeholder="New Task"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WeekView;
