import React, { useState, useEffect } from 'react';
import apiService from '../apiService';
import './TaskView.css';
import { useCalendar } from './CalendarContext';

const TaskView = () => {
    const { selectedDate, setSelectedDate } = useCalendar(); // Directly using the selected date from CalendarContext
    const [newTask, setNewTask] = useState("");
    const [dayTasks, setDayTasks] = useState([]);

    // Helper function to format the date to a readable string format
    const formatSelectedDate = (date) => {
        const dateObj = new Date(date); // Convert the selected date string to a Date object
        return dateObj.toLocaleDateString('en-US', {
            weekday: 'long',
            month: 'long',
            day: 'numeric',
            year: 'numeric'
        });
    };

    // Helper function to format date to YYYY-MM-DD in local timezone
    const formatDateToYYYYMMDD = (date) => {
        const dateObj = new Date(date);
        const year = dateObj.getFullYear();
        const month = String(dateObj.getMonth() + 1).padStart(2, '0'); // Months are zero-indexed
        const day = String(dateObj.getDate()).padStart(2, '0');
        return `${year}-${month}-${day}`;
    };

    // Fetch tasks whenever the selected date changes
    useEffect(() => {
        const fetchTasksForDay = async () => {
            const formattedDate = formatDateToYYYYMMDD(selectedDate); // Ensure date is formatted correctly for API
            try {
                const response = await apiService.getTasksByDate(formattedDate);
                setDayTasks(response);
            } catch (error) {
                console.error('Error fetching tasks for the selected day:', error);
            }
        };

        fetchTasksForDay();
    }, [selectedDate]);

    const handleAddTask = async () => {
        if (newTask.trim()) {
            try {
                const taskData = {
                    name: newTask,
                    completed: false,
                    date: formatDateToYYYYMMDD(selectedDate), // Ensure correct date format
                };
                const response = await apiService.addTask(taskData);
                setDayTasks(prev => [...prev, response]);
                setNewTask("");
            } catch (error) {
                console.error('Error adding task:', error);
            }
        }
    };

    const handleToggleCompletion = async (taskId) => {
        try {
            const updatedTask = await apiService.toggleTaskCompletion(taskId);
            setDayTasks(prev => prev.map(task => task.id === taskId ? updatedTask : task));
        } catch (error) {
            console.error('Error toggling task completion:', error);
        }
    };

    const handleDeleteTask = async (taskId) => {
        try {
            await apiService.deleteTask(taskId);
            setDayTasks(prev => prev.filter(task => task.id !== taskId));
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    const navigateDay = (direction) => {
        const newDate = new Date(selectedDate);
        newDate.setUTCDate(newDate.getUTCDate() + (direction === 'next' ? 1 : -1)); // Ensure UTC modification
        setSelectedDate(formatDateToYYYYMMDD(newDate)); // Set the new selected date in the correct format
    };

    const resetToToday = () => {
        setSelectedDate(formatDateToYYYYMMDD(new Date())); // Reset to today's date in the correct format
    };

    return (
        <div className="task-view-container">
            <div className="task-view-controls">
                <button onClick={() => navigateDay('previous')}>Previous Day</button>
                <button onClick={resetToToday}>Reset to Today</button>
                <button onClick={() => navigateDay('next')}>Next Day</button>
            </div>
            <h2>Tasks for {formatSelectedDate(selectedDate)}</h2> {/* Correctly formatted selected date */}
            <div className="task-table">
                <div className="task-table-header">
                    <div className="task-table-cell date-cell">Date</div>
                    <div className="task-table-cell tasks-cell">Tasks</div>
                </div>
                <div className="task-table-row">
                    <div className="task-table-cell date-cell">
                        {formatSelectedDate(selectedDate)} {/* Ensure the correct date is displayed */}
                    </div>
                    <div className="task-table-cell tasks-cell">
                        <ul className="task-list">
                            {dayTasks.map(task => (
                                <li key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
                                    <span onClick={() => handleToggleCompletion(task.id)}>
                                        {task.name}
                                    </span>
                                    <button
                                        onClick={() => handleDeleteTask(task.id)}
                                        className="delete-task-button"
                                        aria-label="Delete task"
                                    >
                                        ❌
                                    </button>
                                </li>
                            ))}
                        </ul>
                        <div className="task-input-container">
                            <input
                                type="text"
                                value={newTask}
                                onChange={(e) => setNewTask(e.target.value)}
                                placeholder="Enter a new task..."
                            />
                            <button onClick={handleAddTask}>Add Task</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TaskView;
