import React, { useState, useEffect } from 'react';
import apiService from '../apiService';
import './TaskView.css';
import { useCalendar } from './CalendarContext';

const TaskView = () => {
    const { selectedDate, setSelectedDate } = useCalendar(); // Get selected date from CalendarContext
    const [newTask, setNewTask] = useState("");
    const [tasks, setTasks] = useState([]);

    // Helper function to format date to YYYY-MM-DD without timezone shift (forcing UTC)
    const formatDateToYYYYMMDD = (date) => {
        const utcDate = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));
        return utcDate.toISOString().split('T')[0]; // Format as YYYY-MM-DD
    };

    // Function to calculate the start (Monday) and end (Sunday) of the week
    const getMondayAndSunday = (selectedDate) => {
        const date = new Date(selectedDate);
        const day = date.getDay(); // Get the day of the week (0-6, Sunday-Saturday)
        const dayAdjusted = (day + 6) % 7; // Adjust to make Monday 0, ..., Sunday 6

        // Calculate Monday of the current week
        const monday = new Date(date);
        monday.setDate(date.getDate() - dayAdjusted);

        // Calculate Sunday of the current week
        const sunday = new Date(monday);
        sunday.setDate(monday.getDate() + 6);

        return { monday, sunday };
    };

    const { monday, sunday } = getMondayAndSunday(selectedDate); // Calculate week range

    // Generate an array representing each day in the week from Monday to Sunday
    const daysOfWeek = Array.from({ length: 7 }, (_, index) => {
        const day = new Date(monday);
        day.setDate(monday.getDate() + index);
        return day;
    });

    // Fetch tasks for the entire week whenever the selected date changes
    useEffect(() => {
        const fetchTasksForWeek = async () => {
            try {
                const startDate = formatDateToYYYYMMDD(monday);
                const endDate = formatDateToYYYYMMDD(sunday);
                const response = await apiService.getTasks({
                    startDate,
                    endDate
                });
                setTasks(response);
            } catch (error) {
                console.error('Error fetching tasks for the week:', error);
            }
        };

        fetchTasksForWeek();
    }, [monday, sunday]);

    const handleAddTask = async (date) => {
        if (newTask.trim()) {
            try {
                const taskData = {
                    name: newTask,
                    completed: false,
                    date: formatDateToYYYYMMDD(date),
                };
                const response = await apiService.addTask(taskData);
                setTasks(prevTasks => [...prevTasks, response]);
                setNewTask("");
            } catch (error) {
                console.error('Error adding task:', error);
            }
        }
    };

    const handleToggleCompletion = async (taskId) => {
        try {
            const updatedTask = await apiService.toggleTaskCompletion(taskId);
            setTasks(prevTasks => prevTasks.map(task => task.id === taskId ? updatedTask : task));
        } catch (error) {
            console.error('Error toggling task completion:', error);
        }
    };

    const handleDeleteTask = async (taskId) => {
        try {
            await apiService.deleteTask(taskId);
            setTasks(prevTasks => prevTasks.filter(task => task.id !== taskId));
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    const navigateWeek = (direction) => {
        const newDate = new Date(selectedDate);
        newDate.setDate(newDate.getDate() + (direction === 'next' ? 7 : -7));
        setSelectedDate(formatDateToYYYYMMDD(newDate));
    };

    const resetToToday = () => {
        setSelectedDate(formatDateToYYYYMMDD(new Date()));
    };

    // Helper to get tasks for a specific date
    const getTasksForDate = (date) => {
        const dateString = formatDateToYYYYMMDD(date); // Format date as 'YYYY-MM-DD'
        return tasks.filter(task => task.date === dateString);
    };

    return (
        <div className="task-view-container">
            <div className="task-view-controls">
                <button onClick={() => navigateWeek('previous')}>Previous Week</button>
                <button onClick={resetToToday}>Reset to Today</button>
                <button onClick={() => navigateWeek('next')}>Next Week</button>
            </div>
            <h2>{`Tasks for the Week: ${monday.toLocaleDateString()} - ${sunday.toLocaleDateString()}`}</h2>
            <div className="task-table">
                <div className="task-table-header">
                    {daysOfWeek.map(date => (
                        <div key={date.toISOString()} className="task-table-cell date-cell">
                            {date.toLocaleDateString('en-US', { weekday: 'short'})}
                            <br />
                            {date.getDate()}
                        </div>
                    ))}
                </div>
                <div className="task-table-row">
                    {daysOfWeek.map(date => (
                        <div key={date.toISOString()} className="task-table-cell tasks-cell">
                            <ul className="task-list">
                                {getTasksForDate(date).map(task => (
                                    <li key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
                                        <span onClick={() => handleToggleCompletion(task.id)}>
                                            {task.name}
                                        </span>
                                        <button
                                            onClick={() => handleDeleteTask(task.id)}
                                            className="delete-task-button"
                                            aria-label="Delete task"
                                        >
                                            ‚ùå
                                        </button>
                                    </li>
                                ))}
                            </ul>
                            <div className="task-input-container">
                                <input
                                    type="text"
                                    value={newTask}
                                    onChange={(e) => setNewTask(e.target.value)}
                                    placeholder={`Add task for ${date.toLocaleDateString('en-US', { weekday: 'short' })}`}
                                />
                                <button onClick={() => handleAddTask(date)}>Add Task</button>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default TaskView;
