// src/apiService.js
import axios from 'axios';

const API_URL = 'http://localhost:8000/api/';

export const getHabits = () => {
    return axios.get(`${API_URL}habits/`);
};

export const createHabit = (habit) => {
    return axios.post(`${API_URL}habits/`, habit);
};

export const updateHabit = (id, habit) => {
    return axios.put(`${API_URL}habits/${id}/`, habit);
};

export const deleteHabit = (id) => {
    return axios.delete(`${API_URL}habits/${id}/`);
};

// New function to get habit entries by date range
export const getHabitEntriesByDateRange = (startDate, endDate) => {
    return axios.get(`${API_URL}habit-entries/?start_date=${startDate}&end_date=${endDate}`);
};

// New function to create a habit entry
export const createHabitEntry = (habitEntry) => {
    return axios.post(`${API_URL}habit-entries/`, habitEntry);
};

// New function to update a habit entry
export const updateHabitEntry = (id, habitEntry) => {
    return axios.put(`${API_URL}habit-entries/${id}/`, habitEntry);
};

// New function to delete a habit entry
export const deleteHabitEntry = (id) => {
    return axios.delete(`${API_URL}habit-entries/${id}/`);
};
