import React, { useState, useEffect, useCallback } from 'react';
import { getTasks, createTask } from '../apiService';
import './WeekView.css';

const WeekView = ({ currentDate }) => {
    const [tasks, setTasks] = useState({
        Monday: [], Tuesday: [], Wednesday: [], Thursday: [], Friday: [], Saturday: [], Sunday: []
    });

    const [newTask, setNewTask] = useState({
        Monday: '', Tuesday: '', Wednesday: '', Thursday: '', Friday: '', Saturday: '', Sunday: ''
    });

    const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

    const getWeekStartDate = () => {
        const date = new Date(currentDate);
        const day = date.getDay();
        const diff = date.getDate() - day + (day === 0 ? -6 : 1); // Adjust to start on Monday
        return new Date(date.setDate(diff));
    };

    const startOfWeek = getWeekStartDate();
    const endOfWeek = new Date(startOfWeek);
    endOfWeek.setDate(startOfWeek.getDate() + 6);

    const getDateForDay = (dayIndex) => {
        const date = new Date(startOfWeek);
        date.setDate(startOfWeek.getDate() + dayIndex);
        console.log('Calculated date for', daysOfWeek[dayIndex], ':', date); // Debugging line
        return date.toISOString().split('T')[0];
    };

    const fetchTasks = useCallback(async () => {
        try {
            const start = startOfWeek.toISOString().split('T')[0];
            const end = endOfWeek.toISOString().split('T')[0];
            const response = await getTasks(start, end);

            console.log('Fetched tasks:', response.data); // Debugging line

            if (response.data) {
                const fetchedTasks = {
                    Monday: [], Tuesday: [], Wednesday: [], Thursday: [], Friday: [], Saturday: [], Sunday: []
                };

                response.data.forEach(task => {
                    const taskDate = new Date(task.date);
                    const dayIndex = (taskDate.getDay() + 6) % 7;  // Adjust for Sunday as the start of the week
                    const dayName = daysOfWeek[dayIndex];
                    console.log('Mapping task to day:', dayName, task.name);  // Debugging line
                    fetchedTasks[dayName].push({ task: task.name, completed: task.completed });
                });

                setTasks(fetchedTasks);
            }
        } catch (error) {
            console.error('Error fetching tasks:', error);
        }
    }, [startOfWeek, endOfWeek, daysOfWeek]);

    useEffect(() => {
        if (!(currentDate instanceof Date) || isNaN(currentDate.getTime())) {
            console.error('Invalid date:', currentDate);
            return;
        }

        fetchTasks();
    }, [currentDate, fetchTasks]);

    const handleAddTask = async (day, task) => {
        if (task.trim()) {
            const date = getDateForDay(daysOfWeek.indexOf(day));

            try {
                const response = await createTask({
                    name: task,
                    completed: false,
                    date: date
                });

                if (response.status === 201) {
                    setTasks(prevTasks => ({
                        ...prevTasks,
                        [day]: [...prevTasks[day], { task, completed: false }]
                    }));
                    setNewTask(prevNewTask => ({ ...prevNewTask, [day]: '' }));
                } else {
                    console.error('Failed to add task:', response);
                }
            } catch (error) {
                console.error('Error adding task:', error);
            }
        }
    };

    const handleToggleTask = (day, index) => {
        setTasks(prevTasks => ({
            ...prevTasks,
            [day]: prevTasks[day].map((t, i) => i === index ? { ...t, completed: !t.completed } : t)
        }));
    };

    const handleInputChange = (day, event) => {
        setNewTask(prevNewTask => ({ ...prevNewTask, [day]: event.target.value }));
    };

    const handleInputKeyPress = (day, event) => {
        if (event.key === 'Enter') {
            handleAddTask(day, newTask[day]);
        }
    };

    return (
        <div className="week-view">
            <table className="week-table">
                <thead>
                    <tr>
                        <th>Day</th>
                        <th>Tasks</th>
                    </tr>
                </thead>
                <tbody>
                    {daysOfWeek.map((day, index) => (
                        <tr key={day}>
                            <td className="day-column">
                                {day.charAt(0)}
                                <br />
                                {getDateForDay(index)}
                            </td>
                            <td>
                                <ul>
                                    {tasks[day].map((task, taskIndex) => (
                                        <li key={taskIndex} className={task.completed ? 'completed-task' : ''} onClick={() => handleToggleTask(day, taskIndex)}>
                                            {task.task}
                                        </li>
                                    ))}
                                </ul>
                                <input
                                    type="text"
                                    value={newTask[day]}
                                    onChange={(event) => handleInputChange(day, event)}
                                    onKeyPress={(event) => handleInputKeyPress(day, event)}
                                    placeholder="New Task"
                                />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default WeekView;
