import React, { useState, useEffect } from 'react';
import apiService from '../apiService'; // Import your updated API service for fetching tasks
import './TaskView.css'; // Import the CSS for TaskView
import { useCalendar } from './CalendarContext'; // Use the custom hook

const TaskView = () => {
    const { selectedDate } = useCalendar(); // Use the custom hook to access the selected date
    const [tasks, setTasks] = useState([]);
    const [taskInput, setTaskInput] = useState({});

    // Function to calculate the start and end date of the week for the selected date
    const getWeekDateRange = (date) => {
        const startDate = new Date(date);
        const endDate = new Date(date);
        const day = startDate.getDay();
        startDate.setDate(startDate.getDate() - day + (day === 0 ? -6 : 1)); // Set to Monday
        endDate.setDate(startDate.getDate() + 6); // Set to Sunday
        return { startDate: startDate.toISOString().split('T')[0], endDate: endDate.toISOString().split('T')[0] };
    };

    useEffect(() => {
        const fetchTasks = async () => {
            const { startDate, endDate } = getWeekDateRange(selectedDate);
            try {
                const response = await apiService.getTasksByDateRange(startDate, endDate);
                setTasks(response);
                // Initialize task inputs for each day
                const taskInputs = {};
                for (let i = 0; i < 7; i++) {
                    const date = new Date(startDate);
                    date.setDate(date.getDate() + i);
                    taskInputs[date.toISOString().split('T')[0]] = '';
                }
                setTaskInput(taskInputs);
            } catch (error) {
                console.error('Error fetching tasks:', error);
            }
        };

        fetchTasks();
    }, [selectedDate]);

    const handleAddTask = async (date) => {
        const taskName = taskInput[date];
        if (taskName.trim()) {
            try {
                const taskData = {
                    name: taskName,
                    completed: false,
                    date: date
                };
                const response = await apiService.addTask(taskData);
                setTasks([...tasks, response]);
                setTaskInput({ ...taskInput, [date]: '' });
            } catch (error) {
                console.error('Error adding task:', error);
            }
        }
    };

    const handleToggleCompletion = async (taskId) => {
        try {
            const updatedTask = await apiService.toggleTaskCompletion(taskId);
            setTasks(tasks.map(task => task.id === taskId ? updatedTask : task));
        } catch (error) {
            console.error('Error toggling task completion:', error);
        }
    };

    const handleDeleteTask = async (taskId) => {
        try {
            await apiService.deleteTask(taskId);
            setTasks(tasks.filter(task => task.id !== taskId));
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    const { startDate, endDate } = getWeekDateRange(selectedDate);
    const daysOfWeek = Array.from({ length: 7 }, (_, i) => {
        const date = new Date(startDate);
        date.setDate(date.getDate() + i);
        return date.toISOString().split('T')[0];
    });

    return (
        <div className="task-view-container">
            <h2>Tasks for the week of {startDate}</h2>
            <div className="task-table">
                <div className="task-table-header">
                    <div className="task-table-cell">Date</div>
                    <div className="task-table-cell">Tasks</div>
                </div>
                {daysOfWeek.map(date => (
                    <div key={date} className="task-table-row">
                        <div className="task-table-cell date-cell">{date}</div>
                        <div className="task-table-cell tasks-cell">
                            <ul>
                                {tasks.filter(task => task.date === date).map(task => (
                                    <li key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
                                        <span onClick={() => handleToggleCompletion(task.id)}>
                                            {task.name}
                                        </span>
                                        <button onClick={() => handleDeleteTask(task.id)} className="delete-task-button">
                                            ‚ùå
                                        </button>
                                    </li>
                                ))}
                            </ul>
                            <input
                                type="text"
                                value={taskInput[date]}
                                onChange={(e) => setTaskInput({ ...taskInput, [date]: e.target.value })}
                                placeholder="Enter a new task..."
                            />
                            <button onClick={() => handleAddTask(date)}>Add Task</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TaskView;
