import React, { createContext, useState, useContext } from 'react';

// Create a Context for the calendar
const CalendarContext = createContext();

export const CalendarProvider = ({ children }) => {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]); // Default to today

  // Function to get the start and end of the week based on the current date
  const getWeekDateRange = () => {
    const startOfWeek = new Date(selectedDate);
    startOfWeek.setDate(startOfWeek.getDate() - ((startOfWeek.getDay() + 6) % 7)); // Monday as first day

    const endOfWeek = new Date(startOfWeek);
    endOfWeek.setDate(endOfWeek.getDate() + 6);

    return {
      start: startOfWeek.toISOString().split('T')[0],
      end: endOfWeek.toISOString().split('T')[0],
    };
  };

  // Function to handle date change by days
  const changeDate = (days) => {
    const newDate = new Date(selectedDate);
    newDate.setDate(newDate.getDate() + days);
    setSelectedDate(newDate.toISOString().split('T')[0]);
  };

  // Function to format date for display
  const formatDate = (date) => {
    return new Date(date).toLocaleDateString(); // Format date as needed
  };

  return (
    <CalendarContext.Provider value={{ selectedDate, getWeekDateRange, changeDate, formatDate }}>
      {children}
    </CalendarContext.Provider>
  );
};

// Custom hook for using the calendar context
export const useCalendar = () => useContext(CalendarContext);
