import React, { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import apiService from '../apiService';
import './TaskView.css';
import { useCalendar } from './CalendarContext';

const TaskView = () => {
    const { selectedDate, setSelectedDate } = useCalendar();
    const [newTask, setNewTask] = useState("");
    const [tasks, setTasks] = useState([]);

    const formatDateToYYYYMMDD = (date) => {
        const utcDate = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));
        return utcDate.toISOString().split('T')[0];
    };

    const correctedDate = new Date(selectedDate);
    correctedDate.setDate(correctedDate.getDate() + 1);

    const formattedSelectedDate = formatDateToYYYYMMDD(correctedDate);

    useEffect(() => {
        const fetchTasksForDate = async () => {
            try {
                const response = await apiService.getTasksByDate(formattedSelectedDate);
                setTasks(response);
            } catch (error) {
                console.error('Error fetching tasks for the day:', error);
            }
        };

        fetchTasksForDate();
    }, [formattedSelectedDate]);

    const handleAddTask = async () => {
        if (newTask.trim()) {
            try {
                const taskData = {
                    name: newTask,
                    completed: false,
                    date: formattedSelectedDate,
                };
                const response = await apiService.addTask(taskData);
                setTasks(prevTasks => [...prevTasks, response]);
                setNewTask("");
            } catch (error) {
                console.error('Error adding task:', error);
            }
        }
    };

    const handleToggleCompletion = async (taskId) => {
        try {
            const updatedTask = await apiService.toggleTaskCompletion(taskId);
            setTasks(prevTasks => prevTasks.map(task => task.id === taskId ? updatedTask : task));
        } catch (error) {
            console.error('Error toggling task completion:', error);
        }
    };

    const handleDeleteTask = async (taskId) => {
        try {
            await apiService.deleteTask(taskId);
            setTasks(prevTasks => prevTasks.filter(task => task.id !== taskId));
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    const navigateDay = (direction) => {
        const newDate = new Date(correctedDate);
        newDate.setDate(newDate.getDate() + (direction === 'next' ? 1 : -1));
        setSelectedDate(formatDateToYYYYMMDD(newDate));
    };

    const resetToToday = () => {
        setSelectedDate(formatDateToYYYYMMDD(new Date()));
    };

    // Handle drag end event
    const handleDragEnd = (result) => {
        if (!result.destination) return; // If the destination is null (e.g., dropped outside), do nothing

        const updatedTasks = Array.from(tasks);
        const [movedTask] = updatedTasks.splice(result.source.index, 1);
        updatedTasks.splice(result.destination.index, 0, movedTask);

        setTasks(updatedTasks);
    };

    return (
        <div className="task-view-container">
            <div className="task-view-controls">
                <button onClick={() => navigateDay('previous')}>Previous Day</button>
                <button onClick={resetToToday}>Reset to Today</button>
                <button onClick={() => navigateDay('next')}>Next Day</button>
            </div>
            <h2>{`Tasks for ${new Date(correctedDate).toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric', year: 'numeric' })}`}</h2>
            <div className="task-table">
                <div className="task-table-header">
                    <div className="task-table-cell date-cell">Date</div>
                    <div className="task-table-cell tasks-cell">Tasks</div>
                </div>
                <div className="task-table-row">
                    <div className="task-table-cell date-cell">
                        {new Date(correctedDate).toLocaleDateString('en-US', { weekday: 'short' }).charAt(0)} {/* First letter of weekday */}
                        <br />
                        {new Date(correctedDate).getDate()} {/* Numeric day of the month */}
                    </div>
                    <div className="task-table-cell tasks-cell">
                        <DragDropContext onDragEnd={handleDragEnd}>
                            <Droppable droppableId="tasks">
                                {(provided) => (
                                    <ul className="task-list" {...provided.droppableProps} ref={provided.innerRef}>
                                        {tasks.map((task, index) => (
                                            <Draggable key={task.id} draggableId={task.id.toString()} index={index}>
                                                {(provided) => (
                                                    <li
                                                        className={`task-item ${task.completed ? 'completed' : ''}`}
                                                        ref={provided.innerRef}
                                                        {...provided.draggableProps}
                                                        {...provided.dragHandleProps}
                                                    >
                                                        <span onClick={() => handleToggleCompletion(task.id)}>
                                                            {task.name}
                                                        </span>
                                                        <button
                                                            onClick={() => handleDeleteTask(task.id)}
                                                            className="delete-task-button"
                                                            aria-label="Delete task"
                                                        >
                                                            ‚ùå
                                                        </button>
                                                    </li>
                                                )}
                                            </Draggable>
                                        ))}
                                        {provided.placeholder}
                                    </ul>
                                )}
                            </Droppable>
                        </DragDropContext>
                        <div className="task-input-container">
                            <input
                                type="text"
                                value={newTask}
                                onChange={(e) => setNewTask(e.target.value)}
                                placeholder="Enter a new task..."
                            />
                            <button onClick={handleAddTask}>Add Task</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TaskView;
