import React, { useState } from 'react';
import HabitTracker from './HabitTracker'; // Import your HabitTracker component
import TaskView from './TaskView'; // Import your TaskView component
import './Homepage.css'; // Ensure this path is correct

const Homepage = () => {
    const [viewMode, setViewMode] = useState('day'); // or 'week' or 'month'

    const handleViewChange = (mode) => {
        setViewMode(mode);
    };

    return (
        <div className="homepage-container">
            <div className="homepage-controls">
                <button onClick={() => handleViewChange('day')}>Today</button>
                <button onClick={() => handleViewChange('week')}>Week</button>
                <button onClick={() => handleViewChange('month')}>Month</button>
            </div>
            <div className="homepage-content">
                {viewMode === 'week' && <TaskView />}
                {viewMode === 'month' && <HabitTracker viewMode="month" />} {/* Pass viewMode as a prop */}
                {viewMode === 'day' && <HabitTracker viewMode="day" />} {/* Pass viewMode as a prop */}
            </div>
        </div>
    );
};

export default Homepage;
