import React, { useState, useEffect } from 'react';
import apiService from '../apiService'; // Import your API service
import './Journal.css'; // Import the CSS for Journal
import { useCalendar } from './CalendarContext'; // Use the custom hook to access the calendar

const Journal = () => {
    const { selectedDate } = useCalendar(); // Access the selected date from the calendar context
    const [journalEntry, setJournalEntry] = useState("");
    const [existingEntryId, setExistingEntryId] = useState(null); // To store the ID of an existing entry

    useEffect(() => {
        const fetchJournalEntry = async () => {
            try {
                const response = await apiService.getJournalEntryByDate(selectedDate);
                if (response && response.entry_text !== undefined) {
                    setJournalEntry(response.entry_text || ""); // Ensure correct field name
                    setExistingEntryId(response.id || null); // Store the ID of the existing entry
                } else {
                    setJournalEntry(""); // Clear the entry if there's none for the selected date
                    setExistingEntryId(null); // Reset the entry ID
                }
            } catch (error) {
                console.error('Error fetching journal entry:', error);
            }
        };

        fetchJournalEntry();
    }, [selectedDate]);

    const handleSaveJournalEntry = async () => {
        try {
            const journalData = {
                entry_text: journalEntry, // Ensure correct field name
                date: selectedDate,
            };

            if (existingEntryId) {
                // If an entry exists, update it
                await apiService.updateJournalEntry(existingEntryId, journalData);
            } else {
                // Otherwise, create a new entry
                await apiService.createJournalEntry(journalData);
            }
        } catch (error) {
            console.error('Error saving journal entry:', error);
        }
    };

    return (
        <div className="journal-container">
            <textarea
                value={journalEntry}
                onChange={(e) => setJournalEntry(e.target.value)}
                placeholder="Write your thoughts here..."
            />
            <button onClick={handleSaveJournalEntry}>Save Entry</button>
        </div>
    );
};

export default Journal;
