import React, { useState, useEffect } from 'react';
import { getTasks, createTask } from '../apiService'; // Import updated API calls
import './WeekView.css'; // Ensure this file exists for styling

const WeekView = ({ currentDate }) => {
  // State for storing tasks for each day of the week
  const [tasks, setTasks] = useState({
    Monday: [], Tuesday: [], Wednesday: [], Thursday: [], Friday: [], Saturday: [], Sunday: []
  });

  // State for new task input fields
  const [newTask, setNewTask] = useState({
    Monday: '', Tuesday: '', Wednesday: '', Thursday: '', Friday: '', Saturday: '', Sunday: ''
  });

  // Array of days of the week
  const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

  // Calculate the start of the current week based on currentDate
  const startOfWeek = new Date(currentDate);
  startOfWeek.setDate(startOfWeek.getDate() - ((startOfWeek.getDay() + 6) % 7)); // Monday as the first day of the week

  // Function to get the date for a specific day index (0 = Monday, 6 = Sunday)
  const getDateForDay = (dayIndex) => {
    const date = new Date(startOfWeek);
    date.setDate(startOfWeek.getDate() + dayIndex);
    return date.toISOString().split('T')[0]; // Return the full date in ISO format
  };

  // Function to get the date range for the week
  const getWeekDateRange = () => {
    const endOfWeek = new Date(startOfWeek);
    endOfWeek.setDate(startOfWeek.getDate() + 6);
    return {
      start: startOfWeek.toISOString().split('T')[0],
      end: endOfWeek.toISOString().split('T')[0]
    };
  };

  // Fetch tasks for the entire week when the component mounts or when currentDate changes
  useEffect(() => {
    // Ensure currentDate is a valid Date object
    if (!(currentDate instanceof Date) || isNaN(currentDate.getTime())) {
      console.error('Invalid date:', currentDate);
      return;
    }

    const fetchTasks = async () => {
      try {
        const { start, end } = getWeekDateRange();
        const response = await getTasks(start, end); // Use the updated getTasks function from apiService
        if (response.data) {
          const fetchedTasks = {
            Monday: [], Tuesday: [], Wednesday: [], Thursday: [], Friday: [], Saturday: [], Sunday: []
          };

          response.data.forEach(task => {
            const taskDate = new Date(task.date).toISOString().split('T')[0];
            const dayIndex = (new Date(taskDate).getDay() + 6) % 7; // Convert date to day index (0 = Monday, 6 = Sunday)
            const dayName = daysOfWeek[dayIndex];
            fetchedTasks[dayName].push({ task: task.name, completed: task.completed });
          });

          setTasks(fetchedTasks);
        }
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, [currentDate]); // Depend on currentDate to refetch when the week changes

  // Function to handle adding a new task
  const handleAddTask = async (day, task) => {
    if (task.trim()) {
      const date = getDateForDay(daysOfWeek.indexOf(day)); // Get the correct date for the day

      try {
        const response = await createTask({ // Use the updated createTask function from apiService
          name: task,
          completed: false,
          date: date // Store task with the correct date
        });

        if (response.status === 201) {
          // Update tasks state with the new task
          setTasks(prevTasks => ({
            ...prevTasks,
            [day]: [...prevTasks[day], { task, completed: false }]
          }));
          // Clear the input field
          setNewTask(prevNewTask => ({ ...prevNewTask, [day]: '' }));
        } else {
          console.error('Failed to add task:', response);
        }
      } catch (error) {
        console.error('Error adding task:', error);
      }
    }
  };

  // Function to toggle task completion status
  const handleToggleTask = (day, index) => {
    setTasks(prevTasks => ({
      ...prevTasks,
      [day]: prevTasks[day].map((t, i) => i === index ? { ...t, completed: !t.completed } : t)
    }));
  };

  // Handle input change in the task input field
  const handleInputChange = (day, event) => {
    setNewTask(prevNewTask => ({ ...prevNewTask, [day]: event.target.value }));
  };

  // Handle Enter key press in the task input field
  const handleInputKeyPress = (day, event) => {
    if (event.key === 'Enter') {
      handleAddTask(day, newTask[day]);
    }
  };

  // Render the Week View table
  return (
    <div className="week-view">
      <table className="week-table">
        <thead>
          <tr>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {daysOfWeek.map((day, index) => (
            <tr key={day}>
              <td className="day-column">
                {day.charAt(0)}
                <br />
                {getDateForDay(index)}
              </td>
              <td>
                <ul>
                  {tasks[day].map((task, taskIndex) => (
                    <li key={taskIndex} className={task.completed ? 'completed-task' : ''} onClick={() => handleToggleTask(day, taskIndex)}>
                      {task.task}
                    </li>
                  ))}
                </ul>
                <input
                  type="text"
                  value={newTask[day]}
                  onChange={(event) => handleInputChange(day, event)}
                  onKeyPress={(event) => handleInputKeyPress(day, event)}
                  placeholder="New Task"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WeekView;
