import React, { useState } from 'react';
import HabitTracker from './HabitTracker'; // Import the HabitTracker component
import TaskView from './TaskView'; // Import the TaskView component

const Homepage = () => {
  const [activeTab, setActiveTab] = useState('today');

  const renderContent = () => {
    switch (activeTab) {
      case 'today':
        return <HabitTracker viewMode="default" />;
      case 'week':
        return <TaskView />;
      case 'month':
        return <HabitTracker viewMode="month" />;
      default:
        return null;
    }
  };

  return (
    <div className="homepage">
      <div className="tab-buttons">
        <button onClick={() => setActiveTab('today')}>Today</button>
        <button onClick={() => setActiveTab('week')}>Week</button>
        <button onClick={() => setActiveTab('month')}>Month</button>
      </div>
      <div className="tab-content">
        {renderContent()}
      </div>
    </div>
  );
};

export default Homepage;
