import React, { useState, useEffect } from 'react';
import { getHabits, createHabit, deleteHabit, createHabitEntry, updateHabitEntry } from '../apiService';
import './HabitTracker.css'; // Ensure this file exists for styling

const HabitTracker = () => {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState('');
  const [isEditMode, setIsEditMode] = useState(false);
  const [currentDate, setCurrentDate] = useState(new Date().toISOString().split('T')[0]); // Initialize with today's date
  const [viewMode, setViewMode] = useState('default'); // Track view mode

  useEffect(() => {
    fetchHabits();
  }, []);

  const fetchHabits = async () => {
    try {
      const response = await getHabits();
      setHabits(response.data);
    } catch (error) {
      console.error('Error fetching habits:', error);
    }
  };

  const addHabit = async (e) => {
    e.preventDefault();

    if (!newHabit.trim()) {
      console.log('Habit name cannot be empty');
      return;
    }

    const habit = { name: newHabit, description: '' };

    try {
      const response = await createHabit(habit);
      setHabits([...habits, response.data]);
      setNewHabit('');
    } catch (error) {
      console.error('Error creating habit:', error);
    }
  };

  const deleteHabitById = async (habitId) => {
    try {
      await deleteHabit(habitId);
      setHabits(habits.filter(habit => habit.id !== habitId));
    } catch (error) {
      console.error('Error deleting habit:', error);
    }
  };

  const handleToggle = async (habitId, date) => {
    const habit = habits.find(h => h.id === habitId);
    if (!habit) return;

    const entry = habit.entries ? habit.entries.find(e => e.date === date) : null;

    if (entry) {
      try {
        const response = await updateHabitEntry(entry.id, { ...entry, completed: !entry.completed });
        const updatedHabits = habits.map(h => {
          if (h.id === habitId) {
            return { ...h, entries: h.entries.map(e => e.id === entry.id ? response.data : e) };
          }
          return h;
        });
        setHabits(updatedHabits);
      } catch (error) {
        console.error('Error updating habit entry:', error);
      }
    } else {
      try {
        const response = await createHabitEntry({ habit: habitId, date, completed: true });
        const updatedHabits = habits.map(h => {
          if (h.id === habitId) {
            return { ...h, entries: [...(h.entries || []), response.data] };
          }
          return h;
        });
        setHabits(updatedHabits);
      } catch (error) {
        console.error('Error creating habit entry:', error);
      }
    }
  };

  const getStatusSymbol = (status) => {
    if (status) return '✔️'; // Checkmark
    return ''; // No symbol for incomplete
  };

  const handleDateChange = (days) => {
    const newDate = new Date(currentDate);
    newDate.setDate(newDate.getDate() + days);
    setCurrentDate(newDate.toISOString().split('T')[0]);
  };

  const handleDateInputChange = (e) => {
    setCurrentDate(e.target.value);
  };



  const renderDefaultView = () => (
    <table className="habit-table">
      <thead>
        <tr>
          <th>Habit</th>
          <th>Status</th>
          {isEditMode && <th>Actions</th>}
        </tr>
      </thead>
      <tbody>
        {Array.isArray(habits) && habits.length > 0 ? habits.map(habit => {
          const entry = habit.entries ? habit.entries.find(e => e.date === currentDate) : null;
          const isCompleted = entry ? entry.completed : false;
          return (
            <tr key={habit.id}>
              <td>{habit.name}</td>
              <td>
                <button
                  className={`status-button ${isCompleted ? 'completed' : 'not-completed'}`}
                  onClick={() => handleToggle(habit.id, currentDate)}
                >
                  {getStatusSymbol(isCompleted)}
                </button>
              </td>
              {isEditMode && (
                <td>
                  <button onClick={() => deleteHabitById(habit.id)}>Delete</button>
                </td>
              )}
            </tr>
          );
        }) : <tr><td colSpan={isEditMode ? "3" : "2"}>No habits found</td></tr>}
      </tbody>
    </table>
  );

  const renderMonthView = () => {
    const daysInMonth = new Date(currentDate.split('-')[0], currentDate.split('-')[1], 0).getDate();
    const daysArray = Array.from({ length: daysInMonth }, (_, i) => i + 1);

    return (
      <div>
        <input
          type="date"
          value={currentDate}
          onChange={handleDateInputChange}
        />
        <table className="habit-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Day</th>
              {habits.map(habit => (
                <th key={habit.id}>{habit.name}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {daysArray.map(day => {
              const date = new Date(currentDate.split('-')[0], currentDate.split('-')[1] - 1, day);
              const dayLetter = date.toLocaleDateString('en-US', { weekday: 'short' }).charAt(0);
              return (
                <tr key={day}>
                  <td>{day}</td>
                  <td>{dayLetter}</td>
                  {habits.map(habit => {
                    const entry = habit.entries ? habit.entries.find(e => e.date === date.toISOString().split('T')[0]) : null;
                    const isCompleted = entry ? entry.completed : false;
                    return (
                      <td key={habit.id}>
                        <button
                          className={`status-button ${isCompleted ? 'completed' : 'not-completed'}`}
                          onClick={() => handleToggle(habit.id, date.toISOString().split('T')[0])}
                        >
                          {getStatusSymbol(isCompleted)}
                        </button>
                      </td>
                    );
                  })}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div>
      <h1>Habit Tracker</h1>
      <button onClick={() => setIsEditMode(!isEditMode)}>
        {isEditMode ? 'Done' : 'Edit Habits'}
      </button>
      <button onClick={toggleViewMode}>
        {viewMode === 'default' ? 'Switch to Month View' : 'Switch to Default View'}
      </button>
      {isEditMode && (
        <form onSubmit={addHabit}>
          <input
            type="text"
            value={newHabit}
            onChange={(e) => setNewHabit(e.target.value)}
            placeholder="New Habit"
          />
          <button type="submit">Add Habit</button>
        </form>
      )}
      <div className="date-navigation">
        <button onClick={() => handleDateChange(-1)}>←</button>
        <input
          type="date"
          value={currentDate}
          onChange={handleDateInputChange}
        />
        <button onClick={() => handleDateChange(1)}>→</button>
      </div>
      {viewMode === 'default' ? renderDefaultView() : renderMonthView()}
    </div>
  );
};

export default HabitTracker;
