import React, { useState } from 'react';
import TaskView from './TaskView'; // Import TaskView
import HabitTracker from './HabitTracker'; // Import HabitTracker or the appropriate module

const Homepage = () => {
    const [view, setView] = useState('today'); // State to manage which view is displayed

    return (
        <div className="homepage-container">
            <div className="homepage-controls">
                <button onClick={() => setView('today')}>Today</button>
                <button onClick={() => setView('week')}>Week</button>
                <button onClick={() => setView('month')}>Month</button>
            </div>
            <div className="homepage-content">
                {view === 'today' && <HabitTracker />} {/* Replace with your Today view component */}
                {view === 'week' && <TaskView />} {/* Display TaskView for Week view */}
                {view === 'month' && <HabitTracker />} {/* Replace with your Month view component */}
            </div>
        </div>
    );
};

export default Homepage;
