import React, { useState, useEffect } from 'react';
import './HabitTracker.css'; // Ensure this file exists for styling
import { getHabits, createHabit, updateHabit } from '../apiService'; // Import API functions

const HabitTracker = () => {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState('');

  useEffect(() => {
    // Load habits from the server
    getHabits()
      .then(response => setHabits(response.data))
      .catch(error => console.error("Error fetching habits:", error));
  }, []);

  const addHabit = () => {
    const habit = { name: newHabit, completed: {} };
    createHabit(habit)
      .then(response => {
        setHabits([...habits, response.data]);
        setNewHabit('');
      })
      .catch(error => console.error("Error creating habit:", error));
  };

  const handleToggle = (id) => {
    const today = new Date().toISOString().split('T')[0];
    const habit = habits.find(h => h.id === id);
    const newCompleted = { ...habit.completed, [today]: !(habit.completed[today] || false) };

    updateHabit(id, { ...habit, completed: newCompleted })
      .then(() => {
        const updatedHabits = habits.map(h => h.id === id ? { ...h, completed: newCompleted } : h);
        setHabits(updatedHabits);
      })
      .catch(error => console.error("Error updating habit:", error));
  };

  const getStatusSymbol = (status) => {
    return status ? '✔️' : ''; // Checkmark
  };

  return (
    <div>
      <h1>Habit Tracker</h1>
      <input
        type="text"
        value={newHabit}
        onChange={(e) => setNewHabit(e.target.value)}
        placeholder="New Habit"
      />
      <button onClick={addHabit}>Add Habit</button>
      <table className="habit-table">
        <thead>
          <tr>
            <th>Habit</th>
            <th>Completed Today</th>
          </tr>
        </thead>
        <tbody>
          {habits.map((habit) => {
            const today = new Date().toISOString().split('T')[0];
            const isCompleted = habit.completed[today] || false;
            return (
              <tr key={habit.id}>
                <td>{habit.name}</td>
                <td>
                  <button
                    className={`status-button ${isCompleted ? 'completed' : 'not-completed'}`}
                    onClick={() => handleToggle(habit.id)}
                  >
                    {getStatusSymbol(isCompleted)}
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
};

export default HabitTracker;
