// src/components/WeekView.js
import React, { useState, useEffect } from 'react';
import { getTasks, createTask } from '../apiService'; // Ensure correct import
import './WeekView.css'; // Ensure this file exists for styling

const WeekView = ({ currentDate }) => {
  const [tasks, setTasks] = useState({});
  const [newTask, setNewTask] = useState('');
  const [selectedDate, setSelectedDate] = useState('');

  // Calculate the start of the current week based on currentDate
  const startOfWeek = new Date(currentDate);
  startOfWeek.setDate(startOfWeek.getDate() - (startOfWeek.getDay() + 6) % 7); // Monday as the first day of the week

  // Get the date for a specific day index (0 = Monday, 6 = Sunday)
  const getDateForDay = (dayIndex) => {
    const date = new Date(startOfWeek);
    date.setDate(startOfWeek.getDate() + dayIndex);
    return date.toISOString().split('T')[0]; // Return the full date in ISO format
  };

  // Fetch tasks for the selected week
  useEffect(() => {
    if (selectedDate) {
      const fetchTasks = async () => {
        try {
          const response = await getTasks(getDateForDay(0), getDateForDay(6));
          if (response.data) {
            const fetchedTasks = response.data.reduce((acc, task) => {
              const taskDate = new Date(task.date).toISOString().split('T')[0];
              if (!acc[taskDate]) acc[taskDate] = [];
              acc[taskDate].push({ task: task.name, completed: task.completed });
              return acc;
            }, {});
            setTasks(fetchedTasks);
          }
        } catch (error) {
          console.error('Error fetching tasks:', error);
        }
      };
      fetchTasks();
    }
  }, [selectedDate]);

  // Handle adding a new task
  const handleAddTask = async (date) => {
    if (newTask.trim()) {
      try {
        const response = await createTask({
          name: newTask,
          completed: false,
          date: date,
        });
        if (response.status === 201) {
          setTasks((prevTasks) => ({
            ...prevTasks,
            [date]: [...(prevTasks[date] || []), { task: newTask, completed: false }],
          }));
          setNewTask('');
        } else {
          console.error('Failed to add task:', response);
        }
      } catch (error) {
        console.error('Error adding task:', error);
      }
    }
  };

  // Handle input change
  const handleInputChange = (event) => {
    setNewTask(event.target.value);
  };

  // Handle Enter key press
  const handleInputKeyPress = (event, date) => {
    if (event.key === 'Enter') {
      handleAddTask(date);
    }
  };

  const daysOfWeek = Array.from({ length: 7 }, (_, i) => {
    const date = getDateForDay(i);
    return {
      dayName: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'][i],
      date,
    };
  });

  return (
    <div className="week-view">
      <table className="week-table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Tasks</th>
          </tr>
        </thead>
        <tbody>
          {daysOfWeek.map(({ dayName, date }) => (
            <tr key={date}>
              <td>
                {dayName}
                <br />
                {date}
              </td>
              <td>
                <ul>
                  {(tasks[date] || []).map((task, index) => (
                    <li key={index} className={task.completed ? 'completed-task' : ''}>
                      {task.task}
                    </li>
                  ))}
                </ul>
                <input
                  type="text"
                  value={newTask}
                  onChange={handleInputChange}
                  onKeyPress={(event) => handleInputKeyPress(event, date)}
                  placeholder="New Task"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default WeekView;
