import React, { useState, useEffect } from 'react';
import apiService from '../apiService';
import './AdaptiveCalendar.css';

const AdaptiveCalendar = () => {
    const [tasks, setTasks] = useState([]);
    const [scheduledTasks, setScheduledTasks] = useState({});

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await apiService.getTasksByDate(new Date().toISOString().split('T')[0]); // Fetch tasks for today
                setTasks(response);
            } catch (error) {
                console.error('Error fetching tasks:', error);
            }
        };

        fetchTasks();
    }, []);

    const handleDragStart = (e, task) => {
        e.dataTransfer.setData('task', JSON.stringify(task));
    };

    const handleDrop = (e, timeSlot) => {
        const task = JSON.parse(e.dataTransfer.getData('task'));
        setScheduledTasks((prev) => ({
            ...prev,
            [timeSlot]: [...(prev[timeSlot] || []), task],
        }));
    };

    const renderTimeSlots = () => {
        const timeSlots = [];
        for (let hour = 6; hour <= 22; hour++) {
            const time = `${hour}:00`;
            timeSlots.push(
                <div
                    key={time}
                    className="time-slot"
                    onDragOver={(e) => e.preventDefault()}
                    onDrop={(e) => handleDrop(e, time)}
                >
                    <div className="time">{time}</div>
                    <div className="scheduled-tasks">
                        {scheduledTasks[time] && scheduledTasks[time].map((task) => (
                            <div key={task.id} className="task">
                                {task.name}
                            </div>
                        ))}
                    </div>
                </div>
            );
        }
        return timeSlots;
    };

    return (
        <div className="adaptive-calendar">
            <div className="task-list">
                <h3>Tasks</h3>
                {tasks.map((task) => (
                    <div
                        key={task.id}
                        className="task"
                        draggable
                        onDragStart={(e) => handleDragStart(e, task)}
                    >
                        {task.name}
                    </div>
                ))}
            </div>
            <div className="calendar">
                <h3>Schedule</h3>
                {renderTimeSlots()}
            </div>
        </div>
    );
};

export default AdaptiveCalendar;
