import React, { useState, useEffect } from 'react';
import apiService from '../apiService'; // Import your updated API service for fetching tasks
import './TaskView.css'; // Import the CSS for TaskView
import { useCalendar } from './CalendarContext'; // Use the custom hook

const TaskView = () => {
    const { selectedDate, setSelectedDate } = useCalendar(); // Use the custom hook to access the selected date
    const [newTask, setNewTask] = useState("");
    const [dayTasks, setDayTasks] = useState({}); // State to store tasks for the selected date

    // Function to get the day of the week for a given date
    const getDayOfWeek = (date) => {
        const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
        return days[new Date(date).getDay()];
    };

    useEffect(() => {
        const fetchDayTasks = async () => {
            try {
                const response = await apiService.getTasksByDate(selectedDate);
                const groupedTasks = response.reduce((acc, task) => {
                    if (!acc[task.date]) {
                        acc[task.date] = [];
                    }
                    acc[task.date].push(task);
                    return acc;
                }, {});
                setDayTasks(groupedTasks);
            } catch (error) {
                console.error('Error fetching tasks for the day:', error);
            }
        };

        fetchDayTasks();
    }, [selectedDate]);

    const handleAddTask = async () => {
        if (newTask.trim()) {
            try {
                const taskData = {
                    name: newTask,
                    completed: false,
                    date: selectedDate,
                };
                const response = await apiService.addTask(taskData);
                setDayTasks(prev => ({
                    ...prev,
                    [selectedDate]: [...(prev[selectedDate] || []), response]
                }));
                setNewTask("");
            } catch (error) {
                console.error('Error adding task:', error);
            }
        }
    };

    const handleToggleCompletion = async (taskId) => {
        try {
            const updatedTask = await apiService.toggleTaskCompletion(taskId);
            setDayTasks(prev => ({
                ...prev,
                [selectedDate]: prev[selectedDate].map(task => task.id === taskId ? updatedTask : task)
            }));
        } catch (error) {
            console.error('Error toggling task completion:', error);
        }
    };

    const handleDeleteTask = async (taskId) => {
        try {
            await apiService.deleteTask(taskId);
            setDayTasks(prev => ({
                ...prev,
                [selectedDate]: prev[selectedDate].filter(task => task.id !== taskId)
            }));
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    const todayDate = new Date(selectedDate);
    const todayFormatted = todayDate.toISOString().split('T')[0];
    const todayDay = getDayOfWeek(selectedDate);

    const navigateDay = (direction) => {
        const newDate = new Date(selectedDate);
        newDate.setDate(newDate.getDate() + (direction === 'next' ? 1 : -1));
        setSelectedDate(newDate.toISOString().split('T')[0]);
    };

    const resetDay = () => {
        setSelectedDate(new Date().toISOString().split('T')[0]);
    };

    return (
        <div className="task-view-container">
            <div className="task-view-controls">
                <button onClick={() => navigateDay('previous')}>Previous Day</button>
                <button onClick={resetDay}>Reset to Today</button>
                <button onClick={() => navigateDay('next')}>Next Day</button>
            </div>
            <h2>Tasks for {todayFormatted} ({todayDay})</h2>
            <div className="task-table">
                <div className="task-table-header">
                    <div className="task-table-cell date-cell">Date</div>
                    <div className="task-table-cell tasks-cell">Tasks</div>
                </div>
                <div className="task-table-row">
                    <div className="task-table-cell date-cell">
                        {todayFormatted} ({todayDay})
                    </div>
                    <div className="task-table-cell tasks-cell">
                        <ul className="task-list">
                            {(dayTasks[selectedDate] || []).map(task => (
                                <li key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
                                    <span onClick={() => handleToggleCompletion(task.id)}>
                                        {task.name}
                                    </span>
                                    <button
                                        onClick={() => handleDeleteTask(task.id)}
                                        className="delete-task-button"
                                        aria-label="Delete task"
                                    >
                                        ‚ùå
                                    </button>
                                </li>
                            ))}
                        </ul>
                        <div className="task-input-container">
                            <input
                                type="text"
                                value={newTask}
                                onChange={(e) => setNewTask(e.target.value)}
                                placeholder="Enter a new task..."
                            />
                            <button onClick={handleAddTask}>Add Task</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TaskView;
