// src/components/HabitTracker.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function HabitTracker() {
    const [habit, setHabit] = useState('');
    const [habits, setHabits] = useState([]);

    useEffect(() => {
        const fetchHabits = async () => {
            try {
                const response = await axios.get('http://localhost:8000/habits/');
                setHabits(response.data);
            } catch (error) {
                console.error('Error fetching habits', error);
            }
        };

        fetchHabits();
    }, []);

    const handleAddHabit = async () => {
        try {
            const response = await axios.post('http://localhost:8000/habits/', {
                name: habit
            });
            setHabits([...habits, response.data]);
            setHabit('');
        } catch (error) {
            console.error('Error adding habit', error);
        }
    };

    return (
        <div>
            <h1>Habit Tracker</h1>
            <form>
                <input
                    type="text"
                    value={habit}
                    onChange={(e) => setHabit(e.target.value)}
                    placeholder="Enter a new habit"
                />
                <button type="button" onClick={handleAddHabit}>Add Habit</button>
            </form>
            <table>
                <thead>
                    <tr>
                        <th>Habit</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {habits.map(h => (
                        <tr key={h.id}>
                            <td>{h.name}</td>
                            <td>Pending</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default HabitTracker;
