import React, { useState, useEffect } from 'react';
import apiService from '../apiService';
import './TaskView.css';
import { useCalendar } from './CalendarContext';

const TaskView = () => {
    const { selectedDate, setSelectedDate } = useCalendar();
    const [newTask, setNewTask] = useState("");
    const [weekTasks, setWeekTasks] = useState({});

    // Helper function to get the YYYY-MM-DD format without timezone shift
    const formatDateToYYYYMMDD = (date) => {
        return new Date(date.getTime() - date.getTimezoneOffset() * 60000).toISOString().split('T')[0];
    };

    const getWeekRange = (date) => {
        const currentDate = new Date(date);
        const day = currentDate.getDay();
        const start = new Date(currentDate);
        const end = new Date(currentDate);

        // Adjust start to the previous Monday
        start.setDate(currentDate.getDate() - (day === 0 ? 6 : day - 1));
        // Adjust end to the next Sunday
        end.setDate(start.getDate() + 6);

        return {
            start: formatDateToYYYYMMDD(start),
            end: formatDateToYYYYMMDD(end),
        };
    };

    const formatHeaderDate = () => {
        const today = new Date(selectedDate);
        const options = { weekday: 'long', month: 'long', day: 'numeric', year: 'numeric' };
        return `Week of ${today.toLocaleDateString('en-US', options)}`;
    };

    useEffect(() => {
        const fetchWeeklyTasks = async () => {
            const { start, end } = getWeekRange(selectedDate);
            try {
                const response = await apiService.getTasksByDateRange(start, end);
                const groupedTasks = response.reduce((acc, task) => {
                    const formattedDate = formatDateToYYYYMMDD(new Date(task.date));
                    if (!acc[formattedDate]) {
                        acc[formattedDate] = [];
                    }
                    acc[formattedDate].push(task);
                    return acc;
                }, {});
                setWeekTasks(groupedTasks);
            } catch (error) {
                console.error('Error fetching tasks for the week:', error);
            }
        };

        fetchWeeklyTasks();
    }, [selectedDate]);

    const handleAddTask = async (date) => {
        if (newTask.trim()) {
            try {
                // Add task directly for the provided date
                const taskData = {
                    name: newTask,
                    completed: false,
                    date: date, // Use the date directly from the clicked row
                };
                const response = await apiService.addTask(taskData);
                setWeekTasks(prev => ({
                    ...prev,
                    [taskData.date]: [...(prev[taskData.date] || []), response]
                }));
                setNewTask("");
            } catch (error) {
                console.error('Error adding task:', error);
            }
        }
    };

    const handleToggleCompletion = async (taskId) => {
        try {
            const updatedTask = await apiService.toggleTaskCompletion(taskId);
            const date = formatDateToYYYYMMDD(new Date(updatedTask.date));
            setWeekTasks(prev => ({
                ...prev,
                [date]: prev[date].map(task => task.id === taskId ? updatedTask : task)
            }));
        } catch (error) {
            console.error('Error toggling task completion:', error);
        }
    };

    const handleDeleteTask = async (taskId) => {
        try {
            await apiService.deleteTask(taskId);
            const taskDate = Object.keys(weekTasks).find(date => weekTasks[date].some(task => task.id === taskId));
            setWeekTasks(prev => ({
                ...prev,
                [taskDate]: prev[taskDate].filter(task => task.id !== taskId)
            }));
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    const { start } = getWeekRange(selectedDate);
    const startDate = new Date(start);
    let daysOfWeek = Array.from({ length: 7 }, (_, i) => {
        const day = new Date(startDate);
        day.setDate(startDate.getDate() + i);
        return {
            date: formatDateToYYYYMMDD(day), // Ensure date format is consistent (YYYY-MM-DD)
            dayOfWeek: day.toLocaleDateString('en-US', { weekday: 'long' }),
            formattedDate: day.toLocaleDateString('en-US', { day: 'numeric', month: 'long', year: 'numeric' })
        };
    });

    // Adjust the task date mapping to shift tasks one day earlier
    daysOfWeek = [...daysOfWeek.slice(1), daysOfWeek[0]]; // Shift Sunday to the end of the array

    const navigateDay = (direction) => {
        const newDate = new Date(selectedDate);
        newDate.setDate(newDate.getDate() + (direction === 'next' ? 1 : -1));
        setSelectedDate(formatDateToYYYYMMDD(newDate));
    };

    const resetWeek = () => {
        setSelectedDate(formatDateToYYYYMMDD(new Date()));
    };

    return (
        <div className="task-view-container">
            <div className="task-view-controls">
                <button onClick={() => navigateDay('previous')}>Previous Day</button>
                <button onClick={resetWeek}>Reset to Today</button>
                <button onClick={() => navigateDay('next')}>Next Day</button>
            </div>
            <h2>{formatHeaderDate()}</h2>
            <div className="task-table">
                <div className="task-table-header">
                    <div className="task-table-cell date-cell">Date</div>
                    <div className="task-table-cell tasks-cell">Tasks</div>
                </div>
                {daysOfWeek.map(({ date, dayOfWeek, formattedDate }, index) => {
                    return (
                        <div key={date} className="task-table-row">
                            <div className="task-table-cell date-cell">{`${dayOfWeek}, ${formattedDate}`}</div>
                            <div className="task-table-cell tasks-cell">
                                <ul className="task-list">
                                    {(weekTasks[date] ? weekTasks[date] : []).map(task => (
                                        <li key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
                                            <span onClick={() => handleToggleCompletion(task.id)}>
                                                {task.name}
                                            </span>
                                            <button
                                                onClick={() => handleDeleteTask(task.id)}
                                                className="delete-task-button"
                                                aria-label="Delete task"
                                            >
                                                ❌
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                                <div className="task-input-container">
                                    <input
                                        type="text"
                                        value={newTask}
                                        onChange={(e) => setNewTask(e.target.value)}
                                        placeholder="Enter a new task..."
                                    />
                                    <button onClick={() => handleAddTask(date)}>Add Task</button>
                                </div>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default TaskView;
