import React, { useEffect, useState } from 'react';
import './AdaptiveCalendar.css';
import { getTasksByDate } from './apiService';

const AdaptiveCalendar = () => {
    const [tasks, setTasks] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);

    useEffect(() => {
        const fetchTasks = async () => {
            const fetchedTasks = await getTasksByDate(selectedDate);
            setTasks(fetchedTasks);
        };

        fetchTasks();
    }, [selectedDate]);

    const timeSlots = [
        '7:00 AM - 8:00 AM',
        '8:00 AM - 9:00 AM',
        '9:00 AM - 10:00 AM',
        '10:00 AM - 11:00 AM',
        '11:00 AM - 12:00 PM',
    ];

    return (
        <div className="adaptive-calendar-container">
            <table className="adaptive-calendar-table">
                <thead>
                    <tr>
                        <th>Time</th>
                        <th>Tasks</th>
                    </tr>
                </thead>
                <tbody>
                    {timeSlots.map((slot, index) => (
                        <tr key={index}>
                            <td className="time-slot">{slot}</td>
                            <td className="task-slot">
                                {/* Placeholder for draggable task components */}
                                {tasks.map((task) => (
                                    <div key={task.id} className="task-item">
                                        {task.name}
                                    </div>
                                ))}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdaptiveCalendar;
