import React, { useState, useEffect } from 'react';
import apiService from '../apiService'; // Import your updated API service for fetching tasks
import './TaskView.css'; // Import the CSS for TaskView
import { useCalendar } from './CalendarContext'; // Use the custom hook

const TaskView = () => {
    const { selectedDate } = useCalendar(); // Use the custom hook to access the selected date
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState("");

    useEffect(() => {
        // Fetch tasks for the week containing the selected date
        const fetchTasks = async () => {
            const startOfWeek = new Date(selectedDate);
            startOfWeek.setDate(startOfWeek.getDate() - startOfWeek.getDay() + 1); // Set to Monday

            const endOfWeek = new Date(startOfWeek);
            endOfWeek.setDate(startOfWeek.getDate() + 6); // Set to Sunday

            try {
                const response = await apiService.getTasksByDateRange(startOfWeek.toISOString().split('T')[0], endOfWeek.toISOString().split('T')[0]);
                setTasks(response);
            } catch (error) {
                console.error('Error fetching tasks:', error);
            }
        };

        fetchTasks();
    }, [selectedDate]);

    const handleAddTask = async () => {
        if (newTask.trim()) {
            try {
                const taskData = {
                    name: newTask,
                    completed: false,
                    date: selectedDate,
                    // Add more fields like time start/end if needed
                };
                const response = await apiService.addTask(taskData);
                setTasks([...tasks, response]);
                setNewTask("");
            } catch (error) {
                console.error('Error adding task:', error);
            }
        }
    };

    const handleToggleCompletion = async (taskId) => {
        try {
            const updatedTask = await apiService.toggleTaskCompletion(taskId);
            setTasks(tasks.map(task => task.id === taskId ? updatedTask : task));
        } catch (error) {
            console.error('Error toggling task completion:', error);
        }
    };

    const handleDeleteTask = async (taskId) => {
        try {
            await apiService.deleteTask(taskId);
            setTasks(tasks.filter(task => task.id !== taskId));
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    // Helper function to group tasks by date
    const groupTasksByDate = (tasks) => {
        return tasks.reduce((acc, task) => {
            (acc[task.date] = acc[task.date] || []).push(task);
            return acc;
        }, {});
    };

    const tasksByDate = groupTasksByDate(tasks);

    // Generate the dates for the week
    const generateWeekDates = () => {
        const startOfWeek = new Date(selectedDate);
        startOfWeek.setDate(startOfWeek.getDate() - startOfWeek.getDay() + 1); // Set to Monday

        return Array.from({ length: 7 }, (_, i) => {
            const date = new Date(startOfWeek);
            date.setDate(date.getDate() + i);
            return date.toISOString().split('T')[0];
        });
    };

    const weekDates = generateWeekDates();

    return (
        <div className="task-view-container">
            <h2>Tasks for the Week</h2>
            <table className="task-table">
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Tasks</th>
                    </tr>
                </thead>
                <tbody>
                    {weekDates.map(date => (
                        <tr key={date}>
                            <td className="date-column">{date}</td>
                            <td className="tasks-column">
                                <ul className="task-list">
                                    {tasksByDate[date]?.map(task => (
                                        <li key={task.id} className={`task-item ${task.completed ? 'completed' : ''}`}>
                                            <span onClick={() => handleToggleCompletion(task.id)}>
                                                {task.name}
                                            </span>
                                            <button onClick={() => handleDeleteTask(task.id)} className="delete-task-button">
                                                ‚ùå
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div className="task-input-container">
                <input
                    type="text"
                    value={newTask}
                    onChange={(e) => setNewTask(e.target.value)}
                    placeholder="Enter a new task..."
                />
                <button onClick={handleAddTask}>Add Task</button>
            </div>
        </div>
    );
};

export default TaskView;
