import React, { useState, useEffect } from 'react';
import { getHabits, createHabit, deleteHabit, createHabitEntry, updateHabitEntry } from '../apiService';
import './HabitTracker.css'; // Ensure this file exists for styling

const HabitTracker = () => {
  const [habits, setHabits] = useState([]);
  const [entries, setEntries] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date());
  const [viewMode, setViewMode] = useState('default');

  useEffect(() => {
    axios.get('/api/habits/').then(response => {
      setHabits(response.data);
    });

    axios.get('/api/habit-entries/').then(response => {
      setEntries(response.data);
    });
  }, []);

  const handleDateChange = (newDate) => {
    setCurrentDate(new Date(newDate));
  };

  const toggleViewMode = () => {
    setViewMode(viewMode === 'default' ? 'month' : 'default');
  };

  const renderDefaultView = () => (
    <table>
      <thead>
        <tr>
          <th>Habit</th>
          <th>Status</th>
        </tr>
      </thead>
      <tbody>
        {habits.map(habit => (
          <tr key={habit.id}>
            <td>{habit.name}</td>
            <td>{/* Render status */}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );

  const renderMonthView = () => {
    const daysInMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0).getDate();
    const daysArray = Array.from({ length: daysInMonth }, (_, i) => i + 1);

    return (
      <div>
        <input
          type="date"
          value={currentDate.toISOString().substr(0, 10)}
          onChange={(e) => handleDateChange(e.target.value)}
        />
        <table>
          <thead>
            <tr>
              <th>Date</th>
              <th>Day</th>
              {habits.map(habit => (
                <th key={habit.id}>{habit.name}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {daysArray.map(day => {
              const date = new Date(currentDate.getFullYear(), currentDate.getMonth(), day);
              const dayLetter = date.toLocaleDateString('en-US', { weekday: 'short' }).charAt(0);

              return (
                <tr key={day}>
                  <td>{day}</td>
                  <td>{dayLetter}</td>
                  {habits.map(habit => (
                    <td key={habit.id}>
                      {/* Render status */}
                    </td>
                  ))}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div>
      <button onClick={toggleViewMode}>
        {viewMode === 'default' ? 'Switch to Month View' : 'Switch to Default View'}
      </button>
      {viewMode === 'default' ? renderDefaultView() : renderMonthView()}
    </div>
  );
};

export default HabitTracker;
