// src/components/Habits.js
import React, { useState, useEffect } from 'react';
import { getHabits, createHabit, updateHabit, deleteHabit } from '../apiService';

const Habits = () => {
  const [habits, setHabits] = useState([]);
  const [newHabit, setNewHabit] = useState('');

  useEffect(() => {
    fetchHabits();
  }, []);

  const fetchHabits = async () => {
    try {
      const response = await getHabits();
      setHabits(response.data);
    } catch (error) {
      console.error('Error fetching habits:', error);
    }
  };

  const handleCreateHabit = async () => {
    if (newHabit.trim()) {
      try {
        const response = await createHabit({ name: newHabit });
        setHabits([...habits, response.data]);
        setNewHabit('');
      } catch (error) {
        console.error('Error creating habit:', error);
      }
    }
  };

  const handleUpdateHabit = async (id, updatedHabit) => {
    try {
      await updateHabit(id, updatedHabit);
      setHabits(habits.map(habit => (habit.id === id ? updatedHabit : habit)));
    } catch (error) {
      console.error('Error updating habit:', error);
    }
  };

  const handleDeleteHabit = async (id) => {
    try {
      await deleteHabit(id);
      setHabits(habits.filter(habit => habit.id !== id));
    } catch (error) {
      console.error('Error deleting habit:', error);
    }
  };

  return (
    <div>
      <h1>Habits</h1>
      <input
        type="text"
        value={newHabit}
        onChange={(e) => setNewHabit(e.target.value)}
        placeholder="New habit"
      />
      <button onClick={handleCreateHabit}>Add Habit</button>
      <ul>
        {habits.map(habit => (
          <li key={habit.id}>
            <input
              type="text"
              value={habit.name}
              onChange={(e) =>
                handleUpdateHabit(habit.id, { ...habit, name: e.target.value })
              }
            />
            <button onClick={() => handleDeleteHabit(habit.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Habits;
