// CombinedCalendar.js
import React, { createContext, useState, useContext } from 'react';
import './CombinedCalendar.css'; // Ensure you have appropriate styles

// Create a CalendarContext
const CalendarContext = createContext();

export const useCalendar = () => useContext(CalendarContext);

export const CombinedCalendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());

  const incrementDay = () => {
    setCurrentDate(prevDate => new Date(prevDate.getTime() + 24 * 60 * 60 * 1000)); // Add one day
  };

  const decrementDay = () => {
    setCurrentDate(prevDate => new Date(prevDate.getTime() - 24 * 60 * 60 * 1000)); // Subtract one day
  };

  const formatDate = (date) => {
    return date.toLocaleDateString(); // Format date as needed
  };

  return (
    <CalendarContext.Provider value={{ currentDate, incrementDay, decrementDay }}>
      <div className="main-calendar">
        <button onClick={decrementDay}>Previous Day</button>
        <span>{formatDate(currentDate)}</span>
        <button onClick={incrementDay}>Next Day</button>
      </div>
    </CalendarContext.Provider>
  );
};
